import glob from"fast-glob";import*as path from"path";async function importLiam(a){let{meta:t,default:e}=await import(`../pages/liam/${a}`);return{slug:a.replace(/(\/index)?\.mdx$/,""),...t,component:e}}export async function getAllLiam(){let a=await glob(["*.mdx","*/index.mdx"],{cwd:path.join(process.cwd(),"src/pages/liam")});return(await Promise.all(a.map(importLiam))).sort(((a,t)=>new Date(a.date)-new Date(t.date)))}